import React ,{ useState }from 'react'
import { MDBCarousel, MDBCarouselItem } from 'mdb-react-ui-kit';
import "./LandingPage.css"
export default function ImageSlider() {
    let greet = 'Welcome to';
    let greetMessage = 'Your Premier E-Wedding Destination';
    const slides = [
      {
        id: 1,
        imageUrl: 'assets/wedding-image1.jpg',
        text: 'Slide 1 Text',
      },
      {
        id: 2,
        imageUrl: 'assets/wedding-image2.jpg',
        text: 'Slide 2 Text',
      },
      {
        id: 3,
        imageUrl: 'assets/wedding-image3.jpg',
        text: 'Slide 3 Text',
      },
    ];
  
    const [currentSlide, setCurrentSlide] = useState(0);
  
    const nextSlide = () => {
      setCurrentSlide((currentSlide + 1) % slides.length);
    };
  
    const prevSlide = () => {
      setCurrentSlide((currentSlide - 1 + slides.length) % slides.length);
    };
  
    return (
<div className="slider">
      <div className="slide">
        <img src={slides[currentSlide].imageUrl} alt={`Slide ${currentSlide + 1}`} />
        <div className="text-overlay">
          <p>{slides[currentSlide].text}</p>
        </div>
      </div>
      <button onClick={prevSlide}>Prev</button>
      <button onClick={nextSlide}>Next</button>
    </div>
    )
}
