import React ,{ useState }from 'react'
import { MDBCarousel, MDBCarouselItem } from 'mdb-react-ui-kit';
import "./LandingPage.css"
export default function ImageSlider() {
    let greet = 'Welcome to';
    let greetMessage = 'Your Premier E-Wedding Destination';
    const slides = [
      {
        id: 1,
        imageUrl: 'assets/wedding-image1.jpg',
        text: 'Slide 1 Text',
      },
      {
        id: 2,
        imageUrl: 'assets/wedding-image2.jpg',
        text: 'Slide 2 Text',
      },
      {
        id: 3,
        imageUrl: 'assets/wedding-image3.jpg',
        text: 'Slide 3 Text',
      },
    ];
  
    const [currentSlide, setCurrentSlide] = useState(0);
  
    const nextSlide = () => {
      setCurrentSlide((currentSlide + 1) % slides.length);
    };
  
    const prevSlide = () => {
      setCurrentSlide((currentSlide - 1 + slides.length) % slides.length);
    };
  
    return (

        <MDBCarousel className='Images-slider'dark fade interval={1000} >
      <MDBCarouselItem itemId={1}>
        <img src='assets/wedding-image1.jpg' className='w-100 d-block Image-item' alt='Image1' />
        {ShowGreet()}
      </MDBCarouselItem>
      <MDBCarouselItem itemId={2}>
        <img src='assets/wedding-image2.jpg' className='w-100 d-block Image-item' alt='Image2' />
        {ShowGreet()}
      </MDBCarouselItem>
      <MDBCarouselItem itemId={3}>
        <img src='assets/wedding-image3.jpg' className='w-100 d-block Image-item' alt='Image3' />
        {ShowGreet()}
      </MDBCarouselItem>
    </MDBCarousel>
    )
}
