import React, { useState, useEffect } from 'react';
import './Slider.css';

export default function ImageSlider() {
  const greet = `Welcome to <br/> Your Premier E-Wedding Destination`;
  const slides = [
    {
      id: 1,
      imageUrl: 'assets/wedding-image1.jpg',
      text: 'Welcome to <br/> Your Premier E-Wedding Destination',
    },
    {
      id: 2,
      imageUrl: 'assets/wedding-image2.jpg',
      text: greet,
    },
    {
      id: 3,
      imageUrl: 'assets/wedding-image3.jpg',
      text: greet,
    },
  ];

  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((currentSlide + 1) % slides.length);
    }, 2000);

    return () => clearInterval(interval);
  }, [currentSlide, slides.length]);

  return (
    <div className="slider">
      {slides.map((slide, index) => (
        <div
          key={slide.id}
          className={`slide ${index === currentSlide ? 'active' :'hide'}`}
        >
          <div
            className="image-container"
            style={{ backgroundImage: `url(${slide.imageUrl})` }}
          ></div>
            <div className="greeting-message">
            <p dangerouslySetInnerHTML={{ __html: slide.text }}></p>
            </div>
          <div className="text-overlay">
          </div>
        </div>
      ))}
    </div>
  );
};
