import React, { useState, useEffect } from 'react';
import './Slider.css';

export default function ImageSlider() {
  // const title = 'Welcome to';
  // const text='Your Premier E-Wedding Destination';
  const slides = [
    {
      id: 1,
      imageUrl: 'assets/wedding-image1.jpg',
      title: 'Experience the Magic',
      text: 'Embark on a journey where love and technology intertwine to create unforgettable moments.',
    },
    {
      id: 2,
      imageUrl: 'assets/wedding-image2.jpg',
      title: 'Unmatched Elegance',
      text: 'Indulge in luxury as you celebrate your special day in our stunning e-wedding venue.',
    },
    {
      id: 3,
      imageUrl: 'assets/wedding-image3.jpg',
      title: 'Personalized Service',
      text: 'Our team is dedicated to ensuring every detail of your e-wedding is tailored to perfection.',
    },
  ];
  

  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((currentSlide + 1) % slides.length);
    }, 2000);

    return () => clearInterval(interval);
  }, [currentSlide, slides.length]);

  return (
    <div className="slider">
      {slides.map((slide, index) => (
        <div
          key={slide.id}
          className={`slide ${index === currentSlide ? 'active' :'hide'}`}
        >
          <div
            className="image-container"
            style={{ backgroundImage: `url(${slide.imageUrl})` }}
          ></div>
            <div className="greeting-message">
            <span className="greet-title">Welcome to</span>
        <span className="greet-message">Your Premier E-Wedding Destination</span>
            {/* <p dangerouslySetInnerHTML={{ __html: slide.text }}></p> */}
            </div>

        </div>
      ))}
    </div>
  );
};
