import React, { useState, useEffect } from 'react';
import './Slider.css';

export default function ImageSlider() {
  // const title = 'Welcome to';
  // const text='Your Premier E-Wedding Destination';
  const slides = [
    {
      id: 1,
      imageUrl: 'assets/wedding-image1.jpg',
      title: 'Crafting Timeless Memories',
      text: 'Experience the perfect fusion of modern technology and timeless romance, where every moment becomes a cherished memory to last a lifetime.',
    },
    {
      id: 2,
      imageUrl: 'assets/wedding-image2.jpg',
      title: 'Elegance Redefined',
      text: 'Immerse yourself in the epitome of sophistication, where every detail is meticulously curated to create an e-wedding experience that transcends expectations.',
    },
    {
      id: 3,
      imageUrl: 'assets/wedding-image3.jpg',
      title: 'Personalized Perfection',
      text: 'Our dedicated team of experts is committed to bringing your unique vision to life, ensuring that every aspect of your e-wedding reflects your personal style and love story.',
    },
  ];
  

  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((currentSlide + 1) % slides.length);
    }, 4000);

    return () => clearInterval(interval);
  }, [currentSlide, slides.length]);

  return (
    <div className="slider">
      {slides.map((slide, index) => (
        <div
          key={slide.id}
          className={`slide ${index === currentSlide ? 'active' :'hide'}`}
        >
          <div
            className="image-container"
            style={{ backgroundImage: `url(${slide.imageUrl})` }}
          ></div>
            <div className="greeting-message">
            <span className="greet-title">{slide.title}</span>
        <span className="greet-message">{slide.text}</span>
            {/* <p dangerouslySetInnerHTML={{ __html: slide.text }}></p> */}
            </div>

        </div>
      ))}
    </div>
  );
};
