import React ,{ useState,useEffect  }from 'react';
import "./Slider.css"
export default function ImageSlider() {
    let greet = 'Welcome to';
    let greetMessage = 'Your Premier E-Wedding Destination';
    const slides = [
      {
        id: 1,
        imageUrl: 'assets/wedding-image1.jpg',
        text: 'Slide 1 Text',
      },
      {
        id: 2,
        imageUrl: 'assets/wedding-image2.jpg',
        text: 'Slide 2 Text',
      },
      {
        id: 3,
        imageUrl: 'assets/wedding-image3.jpg',
        text: 'Slide 3 Text',
      },
    ];
  
    const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((currentSlide + 1) % slides.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [currentSlide, slides.length]);

  return (
    <div className="slider">
      <div className="slide">
        <img src={slides[currentSlide].imageUrl} alt={`Slide ${currentSlide + 1}`} />
        <div className="text-overlay">
          <p>{slides[currentSlide].text}</p>
        </div>
      </div>
    </div>
  );
};
