import React ,{ useState,useEffect  }from 'react';
import "./Slider.css"
export default function ImageSlider() {
    let greet = 'Welcome to <br/> Your Premier E-Wedding Destination';
    let greetMessage = 'Your Premier E-Wedding Destination';
    const slides = [
      {
        id: 1,
        imageUrl: 'assets/wedding-image1.jpg',
        text: greet,
      },
      {
        id: 2,
        imageUrl: 'assets/wedding-image2.jpg',
        text: greet,
      },
      {
        id: 3,
        imageUrl: 'assets/wedding-image3.jpg',
        text: greet,
      },
    ];
  
    const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((currentSlide + 1) % slides.length);
    }, 3000); // Change slide every 3 seconds (adjust as needed)

    return () => clearInterval(interval);
  }, [currentSlide, slides.length]);

  return (
    <div className="slider">
  {slides.map((slide, index) => (
    <div
      key={slide.id}
      className={`slide ${index === currentSlide ? 'active' : ''}`}
    >
      <div
        className="image-container"
        style={{ backgroundImage: `url(${slide.imageUrl})` }}
      ></div>
      <div className="text-overlay">
        <p>{slide.text}</p>
      </div>
    </div>
  ))}
</div>

  );
};
